mapper.select:
select thumbnail ,title, content, tag
from recipe
where seq=#{seq}


select *
from user
where user_id = #{user_id} and password=#{password}

select seq, content, writer
from reply
where seq=#{seq}

mapper.update:
update recipe
set title=#{title}, content=#{content}, ingredients=#{ingredients}, cookingtime=#{cookingtime}, serving=#{serving}, category=#{category}, regdate=now(), tag=#{tag}
where seq=#{seq}
