mapper.select:
select user_id, name
from user
where user_id=#{user_id} and password=#{password}

select count(*)
from user
where user_id=#{id}

select user_id, password
from user
where user_id=#{user_id} and password=#{password}

select user_id
from user
where email=#{email}

select password
from user
where email=#{email}

select r.seq, r.writer ,r.title, r.thumbnail, r.star, r.regdate, r.tag
from recipe r, user u
where u.user_id = r.writer and u.user_id = #{user_id}
order by r.seq desc;
